#seconde question, nombre de login contenant au moins 1 chiffre#
def contains_number?(item) #analyse chaque login pour détecter la présence d'au moins un chiffre#
    i=0
    (item.length-1).times do
        if (item[i] == "0"||item[i] == "1"||item[i] == "2"||item[i] == "3"||item[i] == "4"||item[i] == "5"||item[i] == "6"||item[i] == "7"||item[i] == "8"||item[i] == "9")
            return true
        end
        i+=1
    end
    return false
end

def number(list)#décompte le nombre de login trouvés précédemment#
    n=0
    list.each do |item|
        if contains_number?(item)
            n+=1
        end
    end
    puts "le nombre d'email possédant au moins un chiffre est: "
    return n
end

#troisième question, login contenant "aude"#
def contains_aude?(item) #analyse chaque login pour repérer ceux contenant aude (majuscule ou minuscule inclus)#
    i=0
    (item.length-3).times do
        if item.downcase[i]=="a"
            if item[i+1]=="u"
                if item[i+2]=="d"
                    if item[i+3]=="e"
                        return true
                    end
                end
            end
        end
        i+=1
    end
    return false
end

def name(list) #décompte le nombre d'occurences trouvés dans la méthode précédente#
    a=0
    list.each do |item|
        if contains_aude?(item)
            a+=1
        end
    end
    return a
end

#quatrième question, login commençant par une majuscule après le @#
def contains_maj?(item)
    if item[1] == item[1].upcase
      return true
    end
end

def maj(list) #décompte le nombre d'occurences trouvés dans la méthode précédente#
    m=0
    list.each do |item|
        if contains_maj?(item)
            m+=1
        else
          m+=0
        end
    end
    return m
end

#cinquième question : au moins une majuscule
def contains_atleastone?(item)
  i=1
  (item.length-1).times do
    if item[i] == item[i].upcase
      return true
    end
    i+=1
  end
  return false
end

def maj1(list)
  p=0
  list.each do |item|
    if contains_atleastone?(item)
      p+=1
    end
  end
  return p 
end

#sixième question : combien d'underscores dans tous les pseudos confondus ?

def underscore(list)
  u=0
  list.each do |item|
    i=1
    while item.length > i
      if item[i] == '_'
        u+=1
      end
      i+=1
    end
  end
  return u 
end

# septième question : trie par ordre alphabétique

def alpha(list)
  list.sort!
  return list
end

# huitième question : Les 50 handles les plus courts

def ordre_court(list)
  list2=list.sort_by(&:length)
  list2.take(49)
end
  
# Neuvième question : quelle est la position dans l'array de @epenser

def position_epenser(list)
  position=list.index("@epenser")
  return position
end

